import { Checksum256, Checksum256Type, UInt8, Variant } from '@greymass/eosio';
/** Chain ID aliases. */
export declare enum ChainName {
    UNKNOWN = 0,
    EOS = 1,
    TELOS = 2,
    JUNGLE = 3,
    KYLIN = 4,
    WORBLI = 5,
    BOS = 6,
    MEETONE = 7,
    INSIGHTS = 8,
    BEOS = 9,
    WAX = 10,
    PROTON = 11,
    FIO = 12
}
export declare type ChainIdType = ChainId | ChainName | Checksum256Type;
export declare class ChainId extends Checksum256 {
    static from<T extends typeof Checksum256>(this: T, value: ChainIdType): InstanceType<T>;
    get chainVariant(): ChainIdVariant;
    get chainName(): ChainName;
}
export declare class ChainAlias extends UInt8 {
    value: ChainName;
}
export declare class ChainIdVariant extends Variant {
    value: ChainId | ChainAlias;
    get chainId(): ChainId;
}
